import React, { useEffect, useRef, useState } from 'react';

export default function Opacity(props) {
  const [opacity, setOpacity] = useState([1, 0.5, 0.5]);

  useEffect(() => {
    const handleIntersection = (entries) => {
      entries.forEach((entry, index) => {
        setOpacity((prevOpacity) => {
          const newOpacity = [...prevOpacity];
          newOpacity[index] = entry.isIntersecting ? 1 : 0.5;
          return newOpacity;
        });
      });
    };

    const options = {
      root: null,
      rootMargin: '0px',
      threshold: 0,
    };

    const observer = new IntersectionObserver(handleIntersection, options);

    props.sectionRefs.forEach((ref) => {
      if (ref.current) {
        observer.observe(ref.current);
      }
    });

    return () => {
      observer.disconnect();
    };
  }, [props.sectionRefs]);

  return (
    <div className="sticky-img">
      {props.sectionRefs.map((ref, index) => (
        <img key={index} src={`/img/renewal-${index + 1}.png`} alt="#" ref={ref} style={{ opacity: opacity[index] }} />
      ))}
    </div>
  );
}
