import React, { useState, useEffect } from 'react';
import QuestionData from './Question.json'; // 파일 경로를 실제 파일 경로로 수정

export default function OverView(props) {
  const [questions, setQuestions] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);

  useEffect(() => {
    // JSON 파일에서 질문 데이터 불러오기
    setQuestions(Object.entries(QuestionData));
  }, []);

  useEffect(() => {
    // 첫 번째와 두 번째 카테고리 선택
    if (questions.length > 1) {
      setSelectedCategories([questions[0][0], questions[1][0]]);
    }
  }, [questions]);

  return (
    <section className='overview-section'>
      <h2>OVERVIEW</h2>
      <div className='renewal-container'>
        <div className='question-container'>
          {selectedCategories.map((selectedCategory, categoryIndex) => (
            <div key={categoryIndex}>
              <h3>{selectedCategory}</h3>
              {selectedCategory &&
                QuestionData[selectedCategory].map((item, index) => (
                  <div key={index}>
                    <p>{item.question}</p>
                    <p>{item.answer}</p>
                  </div>
                ))}
            </div>
          ))}
        </div>
        <figure>
          <img src="#" alt="#" />
        </figure>
      </div>
    </section>
  );
}
