import React, { useState, useEffect } from 'react';
import QuestionData from './Question.json'; // 파일 경로를 실제 파일 경로로 수정

export default function OverView(props) {
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    // JSON 파일에서 질문 데이터 불러오기
    setQuestions(Object.entries(QuestionData));
  }, []);

  // 첫 번째 데이터만 선택
  const [firstCategory, firstItems] = questions.length > 0 ? questions[0] : [];

  return (
    <section className='overview-section'>
      <h2>OVERVIEW</h2>
      <div className='renewal-container'>
        <div className='question-container'>
          <div>
            {firstItems &&
              firstItems.map((item, subIndex) => (
                <div key={subIndex}>
                  <p>{item.question}</p>
                  <p>{item.answer}</p>
                </div>
              ))}
          </div>
        </div>
        <figure>
          <img src="#" alt="#" />
        </figure>
      </div>
    </section>
  );
}
