import React, { useEffect, useRef } from 'react';

export default function Opacity(props) {
  useEffect(() => {
    const handleIntersection = (entries) => {
      entries.forEach((entry, index) => {
        const imgRef = props.sectionRefs[index].current;

        if (entry.isIntersecting) {
          imgRef.style.opacity = 1;
        } else {
          imgRef.style.opacity = 0.5;
        }
      });
    };

    const options = {
      root: null,
      rootMargin: '0px',
      threshold: 0,
    };

    const observer = new IntersectionObserver(handleIntersection, options);
    
    props.sectionRefs.forEach((ref) => {
      if (ref.current) {
        observer.observe(ref.current);
      }
    });

    return () => {
      observer.disconnect();
    };
  }, [props.sectionRefs]);

  return (
    <div className="sticky-img">
      <img src="/img/renewal-1.png" alt="#" ref={props.sectionRefs[0]} />
      <img src="/img/renewal-2.png" alt="#" ref={props.sectionRefs[1]} />
      <img src="/img/renewal-3.png" alt="#" ref={props.sectionRefs[2]} />
    </div>
  );
}
